import React, { useState, useEffect } from "react";
import axios from "../utils/api"; // Make sure the Axios instance is set up

const Dashboard = () => {
  const [users, setUsers] = useState([]);

  // Fetch users from the backend when the component loads
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const { data } = await axios.get("/users");
        setUsers(data);
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    };

    fetchUsers();
  }, []);

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <p>Welcome! Use the navigation bar to manage users and roles.</p>

      <div style={{ marginTop: "2rem" }}>
        <h2>User Overview</h2>
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>S. No</th>
              <th style={styles.th}>Name</th>
              <th style={styles.th}>Role</th>
              <th style={styles.th}>Status</th>
            </tr>
          </thead>
          <tbody>
            {users.length > 0 ? (
              users.map((user, index) => (
                <tr key={user._id} style={styles.tr}>
                  <td style={styles.td}>{index + 1}</td>
                  <td style={styles.td}>{user.username}</td>
                  <td style={styles.td}>{user.role}</td>
                  <td style={styles.td}>{user.status}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td style={styles.td} colSpan="4">
                  No users found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Inline styles for simplicity
const styles = {
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "1rem",
  },
  th: {
    border: "1px solid #ddd",
    padding: "10px",
    textAlign: "left",
    backgroundColor: "#f4f6f9",
    fontWeight: "bold",
  },
  td: {
    border: "1px solid #ddd",
    padding: "10px",
  },
  tr: {
    textAlign: "left",
  },
};

export default Dashboard;
