import React from "react";
import "./Sidebar.css";

const Sidebar = ({ currentUser }) => {
  return (
    <div className="sidebar">
      <h2>Welcome</h2>
      <div className="current-user">
        <p>Name: <span>{currentUser.name}</span></p>
        <p>Role: <span>{currentUser.designation}</span></p>
        <p>Status: <span className={currentUser.status === "Active" ? "active" : "inactive"}>{currentUser.status}</span></p>
      </div>
    </div>
  );
};

export default Sidebar;



