import React, { useState, useEffect } from "react";

const RoleForm = ({ role, onSave }) => {
  const [formData, setFormData] = useState({ name: "", permissions: [] });
  const [permissionInput, setPermissionInput] = useState("");

  useEffect(() => {
    if (role) {
      setFormData(role);
    } else {
      setFormData({ name: "", permissions: [] });
    }
  }, [role]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAddPermission = () => {
    if (permissionInput && !formData.permissions.includes(permissionInput)) {
      setFormData({
        ...formData,
        permissions: [...formData.permissions, permissionInput],
      });
    }
    setPermissionInput("");
  };

  const handleRemovePermission = (permission) => {
    setFormData({
      ...formData,
      permissions: formData.permissions.filter((perm) => perm !== permission),
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        placeholder="Role Name"
        value={formData.name}
        onChange={handleChange}
        required
      />
      <div>
        <input
          type="text"
          placeholder="Add Permission"
          value={permissionInput}
          onChange={(e) => setPermissionInput(e.target.value)}
        />
        <button type="button" onClick={handleAddPermission}>
          Add Permission
        </button>
      </div>
      <ul>
        {formData.permissions.map((perm, index) => (
          <li key={index}>
            {perm} <button type="button" onClick={() => handleRemovePermission(perm)}>Remove</button>
          </li>
        ))}
      </ul>
      <button type="submit">Save Role</button>
    </form>
  );
};

export default RoleForm;
