  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Mocking API call
    const fetchUsers = async () => {
      const mockUsers = [
        { _id: "1", name: "John Doe", designation: "Admin", status: "Active" },
        { _id: "2", name: "Jane Smith", designation: "Normal", status: "Inactive" },
        { _id: "3", name: "Alice Johnson", designation: "Admin", status: "Active" },
      ];
      setUsers(mockUsers);
    };

    fetchUsers();
  }, []);

  return (
    <div className="app">
      <Sidebar users={users} />
      <div className="main-content">
        <h1>Dashboard</h1>
        <p>Welcome to the admin dashboard!</p>
      </div>
    </div>
  );
};

export default App;
