import React, { useEffect, useState } from "react";
import Sidebar from "./components/Sidebar";
import MainContent from "./components/MainContent";
import "./styles/App.css";

const App = () => {
  const [users, setUsers] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    // Mock API call
    const fetchUsers = async () => {
      const mockUsers = [
        { _id: "1", name: "John Doe", designation: "Admin", status: "Active" },
        { _id: "2", name: "Jane Smith", designation: "Normal", status: "Inactive" },
        { _id: "3", name: "Alice Johnson", designation: "Admin", status: "Active" },
      ];
      setUsers(mockUsers);
      setCurrentUser(mockUsers[0]); // Assume the first user is the current logged-in user
    };

    fetchUsers();
  }, []);

  return (
    <div className="app">
      {currentUser && <Sidebar currentUser={currentUser} />}
      <MainContent users={users} />
    </div>
  );
};

export default App;
